import { Component } from '@angular/core';

@Component({
  selector: 'app-checkout',
  templateUrl: './checkout.component.html',
  styleUrls: ['./checkout.component.css']
})
export class CheckoutComponent {
  total = 258; // Ejemplo de total del pedido

  formatCurrency(value: number): string {
    return new Intl.NumberFormat('es-MX', { style: 'currency', currency: 'MXN' }).format(value);
  }

  downloadReceipt() {
    // Simularemos la descarga creando un archivo de texto
    const receiptContent = `Comprobante de pago\n\nTotal: ${this.formatCurrency(this.total)}\nReferencia: #FGB8456\nGracias por tu compra.`;
    const blob = new Blob([receiptContent], { type: 'text/plain' });
    const url = window.URL.createObjectURL(blob);

    const a = document.createElement('a');
    a.href = url;
    a.download = 'comprobante.txt';
    a.click();

    window.URL.revokeObjectURL(url);
  }
}

