/* Contenedor del modal */
.modal-content {
  display: flex;
  justify-content: center;
  align-items: center;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.5);
  z-index: 1000;
}

/* Contenedor principal del modal de producto */
.product-modal {
  display: flex;
  flex-direction: row;
  background: #fff;
  border-radius: 30px;
  overflow: hidden;
  max-width: 876px;
  width: 100%;
  max-height:622px ;
  height: 100%;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
}

/* Contenedor de la imagen */
.product-image-container {
  position: relative;
  width: 300px; /* Ajustar el tamaño del contenedor de imagen */
  height: 300px;
  overflow: hidden;
  border-radius: 20px;
  top: 50px;
  right: -50px;
}

.product-img {
  width: 353px;
  height: 397px;
  object-fit: cover;
  border-radius: 15px; /* Bordes redondeados para que se vea como una tarjeta */
}

/* Ícono del corazón */
.heart-icon {
  position: absolute;
  top: 20px;
  right: 20px;
  width: 40px; /* Tamaño del círculo */
  height: 40px; /* Tamaño del círculo */
  border-radius: 50%;
  background-color: #fff;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: background-color 0.3s;
}

.heart-icon::before {
  font-family: "Font Awesome 5 Free"; /* Usa la fuente de Font Awesome */
  content: "\f004"; /* Código Unicode del icono de corazón */
  font-weight: 900; /* Asegúrate de usar la versión sólida del icono */
  color: #ff6b6b; /* Color del corazón */
  font-size: 20px; /* Ajusta el tamaño del icono */
}

/* Información sobre la imagen del producto */
.product-info-overlay {
  position: absolute;
  bottom: 15px;
  left: 15px;
  color: white;
  text-shadow: 0px 0px 5px rgba(0, 0, 0, 0.5);
}

.overlay-title {
  font-size: 1.3rem;
  font-weight: bold;
}

.overlay-price {
  font-size: 1rem;
  margin-top: 0.5rem;
}

/* Avatares */
.product-avatars {
  display: flex;
  margin-top: 1rem;
}

.avatar {
  width: 28px;
  height: 28px;
  border-radius: 8px; /* Bordes redondeados */
  border: 2px solid #fff;
  margin-right: 0.5rem;
}

/* Contenedor de los detalles del producto */
.product-details {
  padding: 2rem;
  width: 60%; /* Ajustar el tamaño de los detalles del producto */
  
}

/* Título del producto */
.product-title {
  font-size: 1.8rem;
  font-weight: bold;
  margin-bottom: 1rem;
  color: #f87f3d;
  margin-left: 90px; /* Asegúrate de que no haya margen hacia la derecha */
  margin-top: 50px;
}

/* Opciones del producto */
.product-options {
  margin-bottom: 1.5rem;
  margin-left: 90px; /* Asegúrate de que no haya margen hacia la derecha */
  margin-top: 50px;
}

/* Botones de talla */
.size-button {
  background: #f5f5f5;
  padding: 0.5rem 1rem;
  margin-right: 0.5rem;
  border-radius: 8px;
  border: none;
  cursor: pointer;
  transition: background 0.3s;
  font-weight: bold;
}

.size-button.selected {
  background: #61e27d;
  color: #fff;
}

/* Contenedor del color */
.color-container {
  display: flex;
  align-items: center;
  margin-top: 1rem;
}

.color-label {
  margin-right: 1rem;
  font-weight: bold;
}

/* Descripción del producto */
.product-description {
  margin-bottom: 2rem;
  color: #666;
  margin-left: 90px; /* Asegúrate de que no haya margen hacia la derecha */
  margin-top: 50px;
}

/* Acciones del producto */
.product-actions {
  display: flex;
  gap: 1rem;
}

/* Botón de cancelar */
.cancel-button {
  background: transparent;
  border: 2px solid #ccc;
  color: #333;
  padding: 0.5rem 1.5rem;
  border-radius: 12px;
  transition: all 0.3s ease;
}

.cancel-button:hover {
  background: #ccc;
  color: #fff;
}

/* Botón para agregar al carrito */
.add-to-cart-button {
  background: #61e27d;
  color: #fff;
  padding: 0.5rem 1.5rem;
  border-radius: 12px;
  border: none;
  transition: all 0.3s ease;
}

.add-to-cart-button:hover {
  background: #4caf50;
}

